mapper.select:
select t.CNAME, t.STATIONID, t.CTYPE, t.YESTERDAYRAIN, t.TODAYRAIN, t.PERIODRAIN, t.WATERSTAGE, t.WATERFLOW, t.VOLTAGE, t.CURRENTSTATE, t.REPORTTYPE, t.GPRSTYPE, t.DATATIME from CURRENTDATA t where 1=1
and STATIONID = #{StationID}
order by t.STATIONID

mapper.delete:
delete from CURRENTDATA where 1=1
and STATIONID = #{StationID}
AND DATATIME = to_date(#{DataTime},'yyyy-MM-dd HH24:mi:ss')

mapper.select:
select 

from CURRENTDATA
where 
1=1

AND STATIONID = #{stationid,jdbcType=CHAR}


AND CTYPE = #{ctype,jdbcType=VARCHAR}


AND CURRENTSTATE = #{currentstate,jdbcType=VARCHAR}
