mapper.select:
select t.STATIONID, t.STATIONNAME, t.STATIONTYPE, t.SUBCENTERID, t.ORIGIN, t.LONGITUDE, t.LATITUDE, t.ID, t.RAINMAX, t.WATERMAX, t.WATERMIN from STATION_BS t where 1=1
and STATIONID LIKE #{StationID}
and ID LIKE #{ID}
order by t.ID

mapper.delete:
	delete from STATION_BS
	where  1=1
   
   and STATIONID LIKE #{StationID}
