mapper.update:
begin
	
		update VOLTAGE
		
			
			DATA = #{item.data},
			
			
			MESSAGETYPE = #{item.messagetype},
			
			
			STATE = #{item.state}
			
		
		 where 
		STATIONID=#{item.stationid}
		AND DATATIME = to_date(#{item.datatime},'yyyy-MM-dd HH24:mi:ss')
	
;end;

mapper.delete:
delete from VOLTAGE where 1=1
and STATIONID = #{stationid}
AND DATATIME = to_date(#{item.datatime},'yyyy-MM-dd HH24:mi:ss')
